import React, {useState} from 'react';
import styles from "./Orders.module.scss";

const ordersList = [
  {
    id: '2',
    name: 'Кирилл',
    phone: '+79031234567',
    status: 'Ожидает подтверждения оператором'
  },
  {
    id: '3',
    name: 'Андрей',
    phone: '+79033333333',
    status: 'Принят'
  },
  {
    id: '4',
    name: 'Михаил',
    phone: '+79034444444',
    status: 'Отменен'
  },
];

const Orders = () => {
  const [isOpenOrders, setIsOpenOrders] = useState(false);

  const onClickOrdersHandler = () => {
    setIsOpenOrders(!isOpenOrders);
  }

  return (
    <>
      <button className={'btn'} onClick={onClickOrdersHandler}>История заказов</button>
      { isOpenOrders &&
        (<div className={styles.orders_popup}>
          <ul>
            {
              ordersList.map((orderItem, index) =>
              <li key={index}
                  className={styles.orders_item}
              >
                <span>Номер заказа: {orderItem.id}</span>
                <span>Имя клиента: {orderItem.name}</span>
                <span>Телефон: {orderItem.phone}</span>
                <span>Статус: {orderItem.phone}</span>
              </li>
            )}
          </ul>
        </div>)}
    </>
  )
};

export default Orders;
