import React, {useRef, useState} from 'react';
import styles from "./Orders.module.scss";
import cx from "classnames";

const ordersList = [
  {
    id: '1',
    name: 'Кирилл',
    phone: '+79031234567',
    status: 'Ожидает подтверждения оператором'
  },
  {
    id: '2',
    name: 'Андрейcdv df fd vdf fd df df dfv fdvdf',
    phone: '+79033333333',
    status: 'Принят'
  },
  {
    id: '3',
    name: 'Михаил',
    phone: '+79034444444',
    status: 'Отменен'
  },
  {
    id: '4',
    name: 'Маргарита',
    phone: '+79034444в444',
    status: 'Принят'
  },
];

const Orders = () => {
  const [isOpenOrders, setIsOpenOrders] = useState(false);

  const ordersRef = useRef(null);

  // React.useEffect(() => {
  //   const onClickOutside = (event) => {
  //     if (event.target.parentElement.offsetParent !== ordersRef.current) {
  //       setIsOpenOrders(false);
  //     }
  //   };
  //   document.body.addEventListener('click', onClickOutside);
  //   return () => document.body.removeEventListener('click', onClickOutside);
  // }, []);

  const onClickOrdersHandler = () => {
    setIsOpenOrders(!isOpenOrders);
  };

  return (
    <>
      <button ref={ordersRef} className={cx(styles.btn, isOpenOrders ? styles.active : '')} onClick={onClickOrdersHandler}>История заказов</button>
      { isOpenOrders &&
        (<div  className={styles.orders_popup}>
          <table className={styles.orders_list}>
            <thead>
              <tr>
                <th className={styles.orders_id}>Номер заказа</th>
                <th className={styles.orders_name}>Имя клиента</th>
                <th className={styles.orders_phone}>Телефон</th>
                <th className={styles.orders_status}>Статус</th>
              </tr>
            </thead>
            <tbody>
              {
                ordersList.map((orderItem, index) =>
                  <tr key={index}>
                    <td className={styles.orders_id}>{orderItem.id}</td>
                    <td className={styles.orders_name}>{orderItem.name}</td>
                    <td className={styles.orders_phone}>{orderItem.phone}</td>
                    <td className={styles.orders_status}>{orderItem.status}</td>
                  </tr>
                )}
            </tbody>
          </table>
        </div>)}
    </>
  )
};

export default Orders;
